import React from 'react';
import styled from 'styled-components';
import FooterCol from './FooterCol';
import PText from './PText';

const FooterStyle = styled.div`
  background-color: var(--deep-dark);
  padding-top: 10rem;
  .container {
    display: flex;
    gap: 3rem;
  }
  .footer__col1 {
    flex: 2;
  }
  .footer__col2,
  .footer__col3,
  .footer__col4 {
    flex: 1;
  }
  .footer__col1__title {
    font-size: 3.5rem;
    margin-bottom: 1rem;
  }
  .copyright {
    background-color: var(--dark-bg);
    text-align: left;
    padding: 1rem 0;
    margin-top: 5rem;
    .para {
      margin-left: 0;
    }
  }
  @media only screen and (max-width: 768px) {
    .container {
      flex-direction: column;
      gap: 0rem;
      & > div {
        margin-top: 5rem;
      }
    }
    .footer__col1 .para {
      max-width: 100%;
    }
    .copyright {
      .container {
        div {
          margin-top: 0;
        }
      }
    }
  }
`;

export default function Footer() {
  return (
    <FooterStyle>
      <div className="container">
        <div className="footer__col1">
          <h1 className="footer__col1__title">Benjamin Allison</h1>
          <PText>
            MComp Graduate with extensive experience in C# and C++ with a keen
            interest in Visual Image Processing and Augmented Reality.
          </PText>
        </div>
        <div className="footer__col2">
          <FooterCol
            heading="Important Links"
            links={[
              {
                title: 'Home',
                path: '/',
                type: 'Link',
              },
              {
                type: 'Link',
                title: 'About',
                path: '/about',
              },
              {
                type: 'Link',
                title: 'Projects',
                path: '/projects',
              },
              {
                type: 'Link',
                title: 'Contact',
                path: '/contact',
              },
            ]}
          />
        </div>
        <div className="footer__col3">
          <FooterCol
            heading="Contact Info"
            links={[
              {
                title: '07467 457259 ',
                path: 'tel:07467 457259 ',
              },
              {
                title: 'benjamin-allison@hotmail.co.uk ',
                path: 'benjamin-allison@hotmail.co.uk ',
              },
              {
                title: 'Winteringham House, Doncaster, England',
                path:
                  'https://www.google.com/maps/place/Winteringham+House,+Thorpe+in+Balne,+Doncaster+DN6+0EA/@53.5806799,-1.1185361,14z/data=!4m5!3m4!1s0x48791191bb914d27:0xa70a9c97d90185d5!8m2!3d53.5872465!4d-1.1095346',
              },
            ]}
          />
        </div>
        <div className="footer__col4">
          <FooterCol
            heading="Social Links"
            links={[
              {
                title: 'LinkedIn',
                path: 'https://www.linkedin.com/in/benjamin-allison/',
              },
              {
                title: 'GitHub',
                path: 'https://github.com/bwallison',
              },
              {
                title: 'ResearchGate',
                path: 'https://www.researchgate.net/profile/Benjamin-Allison',
              },
            ]}
          />
        </div>
      </div>
      <div className="copyright">
        <div className="container">
          <PText />
        </div>
      </div>
    </FooterStyle>
  );
}
